docReady(()=>{"use strict";var e=document,t=e.querySelector(".grid");
// On identifie la grille isotope
// On identifie le groupe de boutons de filtrage
const r=e.querySelector(".filters"),a=e.querySelector(".ordering");
// On identifie le groupe de boutons d'ordre
// On initialise le composant javaScript Isotope
let s=new Isotope(t,{itemSelector:".grid-item",layoutMode:"fitRows",sortBy:"product",sortAscending:!0}),i=e=>{for(const t of e.getElementsByTagName("a"))t.classList.remove("active")}
// On gère les clicks sur les boutons de filtrage;
// Désactive tous les boutons de filtre
null!==r&&r.addEventListener("click",e=>{e.preventDefault();
// On récupère le bouton cliqué
var e=e.target,t=e.dataset.filter;
// On récupère le nom du filtre
// On désactive tous les boutons de filtre
i(r),
// On active le bouton
e.classList.add("active"),
// On lance le filtrage de l'affichage
s.arrange({filter:t})}),
// On gère les clicks sur les boutons d'ordre
null!==a&&a.addEventListener("click",e=>{e.preventDefault();
// On récupère le bouton cliqué
var e=e.target,t="true"===e.dataset.ordering;
// On récupère le nom du filtre
// On désactive tous les boutons de filtre
i(a),
// On active le bouton
e.classList.add("active"),s.arrange({sortAscending:t})})});